import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on app start
    const token = localStorage.getItem("token");
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      // You could verify the token here if needed
      setIsAuthenticated(true);
      const userData = JSON.parse(localStorage.getItem("user") || "{}");
      setUser(userData);
      setIsAdmin(userData.isAdmin || false);
    }
    setLoading(false);
  }, []);

  const login = async (email, password) => {
    try {
      const response = await axios.post("/api/customers/login", {
        email,
        password,
      });

      const { token, customer_id } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem(
        "user",
        JSON.stringify({ customer_id, email, isAdmin: false })
      );

      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      setUser({ customer_id, email, isAdmin: false });
      setIsAuthenticated(true);
      setIsAdmin(false);

      return { success: true };
    } catch (error) {
      return {
        success: false,
        error: error.response?.data?.error || "Login failed",
      };
    }
  };

  const adminLogin = async (email, password) => {
    try {
      const response = await axios.post("/api/customers/admin/login", {
        email,
        password,
      });

      const { token } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify({ email, isAdmin: true }));

      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      setUser({ email, isAdmin: true });
      setIsAuthenticated(true);
      setIsAdmin(true);

      return { success: true };
    } catch (error) {
      return {
        success: false,
        error: error.response?.data?.error || "Admin login failed",
      };
    }
  };

  const register = async (userData) => {
    try {
      const response = await axios.post("/api/customers/register", userData);

      const { token, customer_id } = response.data;
      localStorage.setItem("token", token);
      localStorage.setItem(
        "user",
        JSON.stringify({
          customer_id,
          email: userData.email,
          isAdmin: false,
        })
      );

      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      setUser({ customer_id, email: userData.email, isAdmin: false });
      setIsAuthenticated(true);
      setIsAdmin(false);

      return { success: true };
    } catch (error) {
      return {
        success: false,
        error: error.response?.data?.error || "Registration failed",
      };
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    delete axios.defaults.headers.common["Authorization"];
    setUser(null);
    setIsAuthenticated(false);
    setIsAdmin(false);
  };

  const value = {
    user,
    isAuthenticated,
    isAdmin,
    loading,
    login,
    adminLogin,
    register,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
