import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";
import {
  Card,
  Button,
  Form,
  Table,
  Alert,
  Modal,
  Badge,
  Row,
  Col,
  Container,
} from "react-bootstrap";

const CustomerDashboard = () => {
  const { user } = useAuth();
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [versionNumber, setVersionNumber] = useState("");
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [showVersionsModal, setShowVersionsModal] = useState(false);
  const [showOverrideModal, setShowOverrideModal] = useState(false);
  const [selectedFileVersions, setSelectedFileVersions] = useState([]);
  const [selectedFileName, setSelectedFileName] = useState("");
  const [overrideFileData, setOverrideFileData] = useState(null);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    try {
      const response = await axios.get("/api/files/my-files");
      setFiles(response.data);
    } catch (error) {
      setError("Failed to fetch files");
    } finally {
      setLoading(false);
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        // 2MB limit
        setError("File size must be less than 2MB");
        setSelectedFile(null);
        return;
      }
      setSelectedFile(file);
      setError("");

      // Auto-suggest next version number if file with same name exists
      const existingFile = files.find(f => f.filename === file.name);
      if (existingFile) {
        setVersionNumber((existingFile.latest_version + 1).toString());
      } else {
        setVersionNumber("1");
      }
    }
  };

  const checkVersionExists = async (filename, version) => {
    try {
      const response = await axios.get(`/api/files/versions/${user.customer_id}/${filename}`);
      const versions = response.data;
      return versions.some(v => v.version === parseInt(version));
    } catch (error) {
      return false;
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!selectedFile) {
      setError("Please select a file");
      return;
    }

    if (!versionNumber || isNaN(parseInt(versionNumber)) || parseInt(versionNumber) < 1) {
      setError("Please enter a valid version number (minimum 1)");
      return;
    }

    const version = parseInt(versionNumber);
    const versionExists = await checkVersionExists(selectedFile.name, version);

    if (versionExists) {
      // Show override confirmation modal
      setOverrideFileData({
        file: selectedFile,
        version: version
      });
      setShowOverrideModal(true);
      return;
    }

    await performUpload(selectedFile, version);
  };

  const performUpload = async (file, version) => {
    setUploading(true);
    setError("");
    setSuccess("");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("version", version.toString());

    try {
      await axios.post("/api/files/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess("File uploaded successfully!");
      setSelectedFile(null);
      setVersionNumber("");
      fetchFiles();
    } catch (error) {
      setError(error.response?.data?.error || "Upload failed");
    } finally {
      setUploading(false);
    }
  };

  const handleOverrideConfirm = async () => {
    if (overrideFileData) {
      await performUpload(overrideFileData.file, overrideFileData.version);
      setShowOverrideModal(false);
      setOverrideFileData(null);
    }
  };

  const handleOverrideCancel = () => {
    setShowOverrideModal(false);
    setOverrideFileData(null);
  };

  const handleDownload = async (filename, version) => {
    try {
      const response = await axios.get(
        `/api/files/download/${user.customer_id}/${filename}/${version}`,
        { responseType: "blob" }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `${filename}_v${version}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      setError("Download failed");
    }
  };

  const handleViewVersions = async (filename) => {
    try {
      const response = await axios.get(
        `/api/files/versions/${user.customer_id}/${filename}`
      );
      setSelectedFileVersions(response.data);
      setSelectedFileName(filename);
      setShowVersionsModal(true);
    } catch (error) {
      setError("Failed to fetch versions");
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div className="text-center">Loading...</div>;
  }

  return (
    <Container>
      <Row>
        <Col>
          <h2 className="mb-4">Customer Dashboard</h2>

          {error && (
            <Alert variant="danger" onClose={() => setError("")} dismissible>
              {error}
            </Alert>
          )}
          {success && (
            <Alert variant="success" onClose={() => setSuccess("")} dismissible>
              {success}
            </Alert>
          )}

          {/* File Upload Section */}
          <Card className="mb-4">
            <Card.Header>
              <h5>Upload New File</h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleUpload}>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Select File (Max 2MB) *</Form.Label>
                      <Form.Control
                        type="file"
                        onChange={handleFileSelect}
                        accept="*/*"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Version Number *</Form.Label>
                      <Form.Control
                        type="number"
                        min="1"
                        placeholder="Enter version number"
                        value={versionNumber}
                        onChange={(e) => setVersionNumber(e.target.value)}
                        required
                      />
                      <Form.Text className="text-muted">
                        Leave empty for auto-increment, or specify a custom version number
                      </Form.Text>
                    </Form.Group>
                  </Col>
                </Row>
                <Button
                  variant="primary"
                  type="submit"
                  disabled={uploading || !selectedFile || !versionNumber}
                >
                  {uploading ? "Uploading..." : "Upload File"}
                </Button>
              </Form>
            </Card.Body>
          </Card>

          {/* Files List */}
          <Card>
            <Card.Header>
              <h5>My Files</h5>
            </Card.Header>
            <Card.Body>
              {files.length === 0 ? (
                <p className="text-muted">No files uploaded yet.</p>
              ) : (
                <Table responsive>
                  <thead>
                    <tr>
                      <th>Filename</th>
                      <th>Latest Version</th>
                      <th>File Size</th>
                      <th>Uploaded</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {files.map((file, index) => (
                      <tr key={index}>
                        <td>{file.filename}</td>
                        <td>
                          <Badge bg="primary">v{file.latest_version}</Badge>
                        </td>
                        <td>{formatFileSize(file.file_size)}</td>
                        <td>{formatDate(file.uploaded_at)}</td>
                        <td>
                          <Button
                            variant="outline-primary"
                            size="sm"
                            className="me-2"
                            onClick={() =>
                              handleDownload(file.filename, file.latest_version)
                            }
                          >
                            Download Latest
                          </Button>
                          <Button
                            variant="outline-secondary"
                            size="sm"
                            onClick={() => handleViewVersions(file.filename)}
                          >
                            View Versions
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Versions Modal */}
      <Modal
        show={showVersionsModal}
        onHide={() => setShowVersionsModal(false)}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>File Versions - {selectedFileName}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Table responsive>
            <thead>
              <tr>
                <th>Version</th>
                <th>File Size</th>
                <th>Uploaded</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {selectedFileVersions.map((version, index) => (
                <tr key={index}>
                  <td>
                    <Badge bg="primary">v{version.version}</Badge>
                  </td>
                  <td>{formatFileSize(version.file_size)}</td>
                  <td>{formatDate(version.uploaded_at)}</td>
                  <td>
                    <Button
                      variant="outline-primary"
                      size="sm"
                      onClick={() =>
                        handleDownload(selectedFileName, version.version)
                      }
                    >
                      Download
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Modal.Body>
      </Modal>

      {/* Override Confirmation Modal */}
      <Modal
        show={showOverrideModal}
        onHide={handleOverrideCancel}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Version Already Exists</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            Version <strong>v{overrideFileData?.version}</strong> of file{" "}
            <strong>{overrideFileData?.file?.name}</strong> already exists.
          </p>
          <p>Do you want to override the existing file with the new one?</p>
          <Alert variant="warning">
            <strong>Warning:</strong> This action cannot be undone. The existing file will be replaced.
          </Alert>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleOverrideCancel}>
            Cancel
          </Button>
          <Button variant="warning" onClick={handleOverrideConfirm}>
            Override File
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default CustomerDashboard;
