import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  Button,
  Table,
  Alert,
  Modal,
  Badge,
  Row,
  Col,
  Container,
  Nav,
  Tab,
  Form,
} from "react-bootstrap";
import {
  Download,
  Trash,
  Eye,
  Upload,
  FileEarmark,
  Person,
  Gear,
} from "react-bootstrap-icons";

const AdminDashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("customers");
  const [showCustomerModal, setShowCustomerModal] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [selectedCustomerFiles, setSelectedCustomerFiles] = useState([]);

  // File upload state
  const [selectedFile, setSelectedFile] = useState(null);
  const [versionNumber, setVersionNumber] = useState("");
  const [uploading, setUploading] = useState(false);
  const [success, setSuccess] = useState("");
  const [selectedCustomerForUpload, setSelectedCustomerForUpload] =
    useState("");

  // Versions modal state
  const [showVersionsModal, setShowVersionsModal] = useState(false);
  const [selectedFileVersions, setSelectedFileVersions] = useState([]);
  const [selectedFileName, setSelectedFileName] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  // Clear success message after 1 minute or when tab changes
  useEffect(() => {
    if (success) {
      const timer = setTimeout(() => {
        setSuccess("");
      }, 60000); // 1 minute

      return () => clearTimeout(timer);
    }
  }, [success]);

  // Clear success message when tab changes
  useEffect(() => {
    setSuccess("");
  }, [activeTab]);

  const fetchData = async () => {
    try {
      const customersResponse = await axios.get("/api/customers/");
      setCustomers(customersResponse.data);
    } catch (error) {
      setError("Failed to fetch data");
    } finally {
      setLoading(false);
    }
  };

  const handleViewCustomerFiles = async (customerId) => {
    try {
      const response = await axios.get(`/api/files/customer/${customerId}`);
      setSelectedCustomerFiles(response.data);
      setSelectedCustomer(customers.find((c) => c.customer_id === customerId));
      setShowCustomerModal(true);
    } catch (error) {
      setError("Failed to fetch customer files");
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        // 2MB limit
        setError("File size must be less than 2MB");
        setSelectedFile(null);
        return;
      }
      setSelectedFile(file);
      setError("");
      setVersionNumber("1");
    }
  };

  const handleAdminUpload = async (e) => {
    e.preventDefault();

    if (!selectedFile) {
      setError("Please select a file");
      return;
    }

    if (!selectedCustomerForUpload) {
      setError("Please select a customer");
      return;
    }

    if (!versionNumber || versionNumber.trim() === "") {
      setError("Please enter a version number");
      return;
    }

    setUploading(true);
    setError("");
    setSuccess("");

    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("customer_id", selectedCustomerForUpload);
    formData.append("version", versionNumber.trim());

    try {
      await axios.post("/api/files/admin/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess("File uploaded successfully!");
      setSelectedFile(null);
      setVersionNumber("");
      setSelectedCustomerForUpload("");
      // Reset the file input
      const fileInput = document.querySelector('input[type="file"]');
      if (fileInput) {
        fileInput.value = "";
      }
      fetchData();
    } catch (error) {
      const errorMessage = error.response?.data?.error || "Upload failed";
      setError(errorMessage);

      // If the error is about version already existing, suggest a new version
      if (
        errorMessage.includes("already exists") &&
        errorMessage.includes("version")
      ) {
        // Try to suggest next version
        try {
          const numVersion = parseFloat(versionNumber);
          if (!isNaN(numVersion)) {
            setVersionNumber((numVersion + 1).toString());
          } else {
            setVersionNumber(versionNumber + "_1");
          }
        } catch {
          setVersionNumber(versionNumber + "_1");
        }
      }
    } finally {
      setUploading(false);
    }
  };

  const handleDownloadFile = async (customerId, filename, version) => {
    try {
      const response = await axios.get(
        `/api/files/download/${customerId}/${filename}/${version}`,
        { responseType: "blob" }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `${filename}_v${version}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      setError("Download failed");
    }
  };

  const handleDeleteFile = async (customerId, filename, version) => {
    if (
      !window.confirm(
        `Are you sure you want to delete ${filename} version ${version}?`
      )
    ) {
      return;
    }

    try {
      await axios.delete(
        `/api/files/admin/delete/${customerId}/${filename}/${version}`
      );

      setSuccess(`File ${filename} version ${version} deleted successfully!`);

      // Refresh the customer files
      if (selectedCustomer) {
        const response = await axios.get(`/api/files/customer/${customerId}`);
        setSelectedCustomerFiles(response.data);
      }

      // Refresh the main data
      fetchData();
    } catch (error) {
      const errorMessage = error.response?.data?.error || "Delete failed";
      setError(errorMessage);
    }
  };

  const handleViewVersions = async (customerId, filename) => {
    try {
      const response = await axios.get(
        `/api/files/versions/${customerId}/${filename}`
      );
      setSelectedFileVersions(response.data);
      setSelectedFileName(filename);
      setShowVersionsModal(true);
    } catch (error) {
      setError("Failed to fetch file versions");
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div className="text-center">Loading...</div>;
  }

  return (
    <Container fluid>
      <Row>
        <Col>
          <h2 className="mb-4">Admin Dashboard</h2>

          {error && (
            <Alert variant="danger" onClose={() => setError("")} dismissible>
              {error}
            </Alert>
          )}

          {success && (
            <Alert variant="success" onClose={() => setSuccess("")} dismissible>
              {success}
            </Alert>
          )}

          <Tab.Container
            activeKey={activeTab}
            onSelect={(k) => setActiveTab(k)}
          >
            <Row>
              <Col>
                <Nav variant="tabs" className="mb-4">
                  <Nav.Item>
                    <Nav.Link eventKey="customers">
                      <Person className="me-2" />
                      Customers ({customers.length})
                    </Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="upload">
                      <Upload className="me-2" />
                      Upload File
                    </Nav.Link>
                  </Nav.Item>
                </Nav>

                <Tab.Content>
                  <Tab.Pane eventKey="customers">
                    <Card>
                      <Card.Header>
                        <h5>Customer Management</h5>
                      </Card.Header>
                      <Card.Body>
                        {customers.length === 0 ? (
                          <p className="text-muted">
                            No customers registered yet.
                          </p>
                        ) : (
                          <Table responsive>
                            <thead>
                              <tr>
                                <th>Customer ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Address</th>
                                <th>Registered</th>
                                <th>Actions</th>
                              </tr>
                            </thead>
                            <tbody>
                              {customers.map((customer, index) => (
                                <tr key={index}>
                                  <td>
                                    <Badge bg="secondary">
                                      {customer.customer_id}
                                    </Badge>
                                  </td>
                                  <td>{customer.name}</td>
                                  <td>{customer.email}</td>
                                  <td>{customer.phone}</td>
                                  <td>{customer.address}</td>
                                  <td>{formatDate(customer.created_at)}</td>
                                  <td>
                                    <Button
                                      variant="outline-primary"
                                      size="sm"
                                      onClick={() =>
                                        handleViewCustomerFiles(
                                          customer.customer_id
                                        )
                                      }
                                    >
                                      View Files
                                    </Button>
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </Table>
                        )}
                      </Card.Body>
                    </Card>
                  </Tab.Pane>

                  <Tab.Pane eventKey="upload">
                    <Card>
                      <Card.Header>
                        <h5>Upload File for Customer</h5>
                      </Card.Header>
                      <Card.Body>
                        <Form onSubmit={handleAdminUpload}>
                          <Row>
                            <Col md={6}>
                              <Form.Group className="mb-3">
                                <Form.Label>Select Customer *</Form.Label>
                                <Form.Select
                                  value={selectedCustomerForUpload}
                                  onChange={(e) =>
                                    setSelectedCustomerForUpload(e.target.value)
                                  }
                                  required
                                >
                                  <option value="">Choose a customer...</option>
                                  {customers.map((customer) => (
                                    <option
                                      key={customer.customer_id}
                                      value={customer.customer_id}
                                    >
                                      {customer.name} ({customer.email})
                                    </option>
                                  ))}
                                </Form.Select>
                              </Form.Group>
                            </Col>
                            <Col md={6}>
                              <Form.Group className="mb-3">
                                <Form.Label>Select File (Max 2MB) *</Form.Label>
                                <Form.Control
                                  type="file"
                                  onChange={handleFileSelect}
                                  accept="*/*"
                                  required
                                />
                              </Form.Group>
                            </Col>
                          </Row>
                          <Row>
                            <Col md={6}>
                              <Form.Group className="mb-3">
                                <Form.Label>Version Number *</Form.Label>
                                <Form.Control
                                  type="text"
                                  placeholder="Enter version (e.g., 1, 1.1, beta, etc.)"
                                  value={versionNumber}
                                  onChange={(e) =>
                                    setVersionNumber(e.target.value)
                                  }
                                  required
                                />
                                <Form.Text className="text-muted">
                                  Enter any version identifier (numbers,
                                  decimals, or text)
                                </Form.Text>
                              </Form.Group>
                            </Col>
                          </Row>
                          <Button
                            variant="primary"
                            type="submit"
                            disabled={
                              uploading ||
                              !selectedFile ||
                              !selectedCustomerForUpload ||
                              !versionNumber.trim()
                            }
                          >
                            {uploading ? (
                              <>
                                <Gear className="me-2" />
                                Uploading...
                              </>
                            ) : (
                              <>
                                <Upload className="me-2" />
                                Upload File
                              </>
                            )}
                          </Button>
                        </Form>
                      </Card.Body>
                    </Card>
                  </Tab.Pane>
                </Tab.Content>
              </Col>
            </Row>
          </Tab.Container>
        </Col>
      </Row>

      {/* Customer Files Modal */}
      <Modal
        show={showCustomerModal}
        onHide={() => setShowCustomerModal(false)}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            Files for {selectedCustomer?.name} ({selectedCustomer?.email})
          </Modal.Title>
          <Button
            variant="outline-success"
            size="sm"
            onClick={() => {
              setSelectedCustomerForUpload(selectedCustomer?.customer_id);
              setActiveTab("upload");
              setShowCustomerModal(false);
            }}
            title="Upload File for This Customer"
          >
            <Upload className="me-1" />
            Upload File
          </Button>
        </Modal.Header>
        <Modal.Body>
          {selectedCustomerFiles.length === 0 ? (
            <p className="text-muted">No files uploaded by this customer.</p>
          ) : (
            <Table responsive>
              <thead>
                <tr>
                  <th>Filename</th>
                  <th>Latest Version</th>
                  <th>File Size</th>
                  <th>Uploaded</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {selectedCustomerFiles.map((file, index) => (
                  <tr key={index}>
                    <td>{file.filename}</td>
                    <td>
                      <Badge bg="primary">v{file.latest_version}</Badge>
                    </td>
                    <td>{formatFileSize(file.file_size)}</td>
                    <td>{formatDate(file.uploaded_at)}</td>
                    <td>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() =>
                          handleDownloadFile(
                            selectedCustomer.customer_id,
                            file.filename,
                            file.latest_version
                          )
                        }
                        title="Download Latest Version"
                      >
                        <Download />
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() =>
                          handleDeleteFile(
                            selectedCustomer.customer_id,
                            file.filename,
                            file.latest_version
                          )
                        }
                        className="ms-2"
                        title="Delete Latest Version"
                      >
                        <Trash />
                      </Button>
                      <Button
                        variant="outline-info"
                        size="sm"
                        onClick={() =>
                          handleViewVersions(
                            selectedCustomer.customer_id,
                            file.filename
                          )
                        }
                        className="ms-2"
                        title="View All Versions"
                      >
                        <Eye />
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Modal.Body>
      </Modal>

      {/* File Versions Modal */}
      <Modal
        show={showVersionsModal}
        onHide={() => setShowVersionsModal(false)}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>All Versions of {selectedFileName}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedFileVersions.length === 0 ? (
            <p className="text-muted">No versions found for this file.</p>
          ) : (
            <Table responsive>
              <thead>
                <tr>
                  <th>Version</th>
                  <th>File Size</th>
                  <th>Uploaded</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {selectedFileVersions.map((file, index) => (
                  <tr key={index}>
                    <td>
                      <Badge bg="primary">v{file.version}</Badge>
                    </td>
                    <td>{formatFileSize(file.file_size)}</td>
                    <td>{formatDate(file.uploaded_at)}</td>
                    <td>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() =>
                          handleDownloadFile(
                            selectedCustomer?.customer_id,
                            file.filename,
                            file.version
                          )
                        }
                        title="Download Version"
                      >
                        <Download />
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() =>
                          handleDeleteFile(
                            selectedCustomer?.customer_id,
                            file.filename,
                            file.version
                          )
                        }
                        className="ms-2"
                        title="Delete Version"
                      >
                        <Trash />
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default AdminDashboard;
