import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

// Function to decode JWT token
const decodeToken = (token) => {
  try {
    const base64Url = token.split(".")[1];
    const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split("")
        .map(function (c) {
          return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
        })
        .join("")
    );
    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error("Error decoding token:", error);
    return null;
  }
};

export function AuthProvider({ children }) {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  // Set up axios interceptor to include token in all requests
  useEffect(() => {
    const interceptor = axios.interceptors.request.use(
      (config) => {
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => {
        return Promise.reject(error);
      }
    );

    // Cleanup interceptor on unmount
    return () => {
      axios.interceptors.request.eject(interceptor);
    };
  }, [token]);

  const login = async (email, password) => {
    try {
      const response = await axios.post(
        "http://localhost:5002/api/customers/login",
        {
          email,
          password,
        }
      );

      if (response.data.token) {
        setToken(response.data.token);
        // Decode token to get user info
        const decodedToken = decodeToken(response.data.token);
        if (decodedToken) {
          setUser({
            ...response.data,
            customer_id: decodedToken.customer_id,
            email: decodedToken.email,
            is_admin: decodedToken.is_admin,
          });
        } else {
          setUser(response.data);
        }
        return { success: true };
      } else {
        return { success: false, error: "Login failed" };
      }
    } catch (error) {
      console.error("Login error:", error);
      return {
        success: false,
        error: error.response?.data?.message || "Login failed",
      };
    }
  };

  const adminLogin = async (email, password) => {
    try {
      const response = await axios.post(
        "http://localhost:5002/api/customers/admin/login",
        {
          email,
          password,
        }
      );

      if (response.data.token) {
        setToken(response.data.token);
        // Decode token to get user info including is_admin
        const decodedToken = decodeToken(response.data.token);
        if (decodedToken) {
          setUser({
            ...response.data,
            customer_id: decodedToken.customer_id,
            email: decodedToken.email,
            is_admin: decodedToken.is_admin,
          });
        } else {
          setUser(response.data);
        }
        return { success: true };
      } else {
        return { success: false, error: "Admin login failed" };
      }
    } catch (error) {
      console.error("Admin login error:", error);
      return {
        success: false,
        error: error.response?.data?.message || "Admin login failed",
      };
    }
  };

  const register = async (userData) => {
    try {
      const response = await axios.post(
        "http://localhost:5002/api/customers/register",
        userData
      );

      if (response.data.token) {
        setToken(response.data.token);
        // Decode token to get user info
        const decodedToken = decodeToken(response.data.token);
        if (decodedToken) {
          setUser({
            ...response.data,
            customer_id: decodedToken.customer_id,
            email: decodedToken.email,
            is_admin: decodedToken.is_admin
          });
        } else {
          setUser(response.data);
        }
        return { success: true };
      } else {
        return { success: false, error: "Registration failed" };
      }
    } catch (error) {
      console.error("Registration error:", error);
      return {
        success: false,
        error: error.response?.data?.message || "Registration failed",
      };
    }
  };

  const logout = () => {
    setToken(null);
    setUser(null);
  };

  const value = {
    token,
    user,
    login,
    adminLogin,
    register,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}
