import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  Button,
  Table,
  Alert,
  Modal,
  Badge,
  Row,
  Col,
  Container,
  Nav,
  Tab,
} from "react-bootstrap";

const AdminDashboard = () => {
  const [customers, setCustomers] = useState([]);
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("customers");
  const [showCustomerModal, setShowCustomerModal] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [selectedCustomerFiles, setSelectedCustomerFiles] = useState([]);

  // File upload state
  const [selectedFile, setSelectedFile] = useState(null);
  const [versionNumber, setVersionNumber] = useState("");
  const [uploading, setUploading] = useState(false);
  const [success, setSuccess] = useState("");
  const [selectedCustomerForUpload, setSelectedCustomerForUpload] =
    useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [customersResponse, filesResponse] = await Promise.all([
        axios.get("/api/customers/"),
        axios.get("/api/files/all"),
      ]);

      setCustomers(customersResponse.data);
      setFiles(filesResponse.data);
    } catch (error) {
      setError("Failed to fetch data");
    } finally {
      setLoading(false);
    }
  };

  const handleViewCustomerFiles = async (customerId) => {
    try {
      const response = await axios.get(`/api/files/customer/${customerId}`);
      setSelectedCustomerFiles(response.data);
      setSelectedCustomer(customers.find((c) => c.customer_id === customerId));
      setShowCustomerModal(true);
    } catch (error) {
      setError("Failed to fetch customer files");
    }
  };

  const handleDownloadFile = async (customerId, filename, version) => {
    try {
      const response = await axios.get(
        `/api/files/download/${customerId}/${filename}/${version}`,
        { responseType: "blob" }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `${filename}_v${version}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      setError("Download failed");
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i];
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return <div className="text-center">Loading...</div>;
  }

  return (
    <Container fluid>
      <Row>
        <Col>
          <h2 className="mb-4">Admin Dashboard</h2>

          {error && (
            <Alert variant="danger" onClose={() => setError("")} dismissible>
              {error}
            </Alert>
          )}

          <Tab.Container
            activeKey={activeTab}
            onSelect={(k) => setActiveTab(k)}
          >
            <Row>
              <Col>
                <Nav variant="tabs" className="mb-4">
                  <Nav.Item>
                    <Nav.Link eventKey="customers">
                      Customers ({customers.length})
                    </Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="files">
                      All Files ({files.length})
                    </Nav.Link>
                  </Nav.Item>
                </Nav>

                <Tab.Content>
                  <Tab.Pane eventKey="customers">
                    <Card>
                      <Card.Header>
                        <h5>Customer Management</h5>
                      </Card.Header>
                      <Card.Body>
                        {customers.length === 0 ? (
                          <p className="text-muted">
                            No customers registered yet.
                          </p>
                        ) : (
                          <Table responsive>
                            <thead>
                              <tr>
                                <th>Customer ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Address</th>
                                <th>Registered</th>
                                <th>Actions</th>
                              </tr>
                            </thead>
                            <tbody>
                              {customers.map((customer, index) => (
                                <tr key={index}>
                                  <td>
                                    <Badge bg="secondary">
                                      {customer.customer_id}
                                    </Badge>
                                  </td>
                                  <td>{customer.name}</td>
                                  <td>{customer.email}</td>
                                  <td>{customer.phone}</td>
                                  <td>{customer.address}</td>
                                  <td>{formatDate(customer.created_at)}</td>
                                  <td>
                                    <Button
                                      variant="outline-primary"
                                      size="sm"
                                      onClick={() =>
                                        handleViewCustomerFiles(
                                          customer.customer_id
                                        )
                                      }
                                    >
                                      View Files
                                    </Button>
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </Table>
                        )}
                      </Card.Body>
                    </Card>
                  </Tab.Pane>

                  <Tab.Pane eventKey="files">
                    <Card>
                      <Card.Header>
                        <h5>All Files</h5>
                      </Card.Header>
                      <Card.Body>
                        {files.length === 0 ? (
                          <p className="text-muted">No files uploaded yet.</p>
                        ) : (
                          <Table responsive>
                            <thead>
                              <tr>
                                <th>Customer ID</th>
                                <th>Filename</th>
                                <th>Version</th>
                                <th>File Size</th>
                                <th>Uploaded</th>
                                <th>Actions</th>
                              </tr>
                            </thead>
                            <tbody>
                              {files.map((file, index) => (
                                <tr key={index}>
                                  <td>
                                    <Badge bg="secondary">
                                      {file.customer_id}
                                    </Badge>
                                  </td>
                                  <td>{file.filename}</td>
                                  <td>
                                    <Badge bg="primary">v{file.version}</Badge>
                                  </td>
                                  <td>{formatFileSize(file.file_size)}</td>
                                  <td>{formatDate(file.uploaded_at)}</td>
                                  <td>
                                    <Button
                                      variant="outline-primary"
                                      size="sm"
                                      onClick={() =>
                                        handleDownloadFile(
                                          file.customer_id,
                                          file.filename,
                                          file.version
                                        )
                                      }
                                    >
                                      Download
                                    </Button>
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </Table>
                        )}
                      </Card.Body>
                    </Card>
                  </Tab.Pane>
                </Tab.Content>
              </Col>
            </Row>
          </Tab.Container>
        </Col>
      </Row>

      {/* Customer Files Modal */}
      <Modal
        show={showCustomerModal}
        onHide={() => setShowCustomerModal(false)}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            Files for {selectedCustomer?.name} ({selectedCustomer?.email})
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedCustomerFiles.length === 0 ? (
            <p className="text-muted">No files uploaded by this customer.</p>
          ) : (
            <Table responsive>
              <thead>
                <tr>
                  <th>Filename</th>
                  <th>Latest Version</th>
                  <th>File Size</th>
                  <th>Uploaded</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {selectedCustomerFiles.map((file, index) => (
                  <tr key={index}>
                    <td>{file.filename}</td>
                    <td>
                      <Badge bg="primary">v{file.latest_version}</Badge>
                    </td>
                    <td>{formatFileSize(file.file_size)}</td>
                    <td>{formatDate(file.uploaded_at)}</td>
                    <td>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() =>
                          handleDownloadFile(
                            selectedCustomer.customer_id,
                            file.filename,
                            file.latest_version
                          )
                        }
                      >
                        Download Latest
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default AdminDashboard;
